document.addEventListener("DOMContentLoaded", () => {
  const loginForm = document.getElementById("loginForm");
  const inputs = loginForm.querySelectorAll(
    'input[type="text"], input[type="email"], input[type="password"]'
  );

  const loginAPI = async (formData) => {
    try {
      const response = await fetch("backend/api/user.php", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(formData),
        credentials: "same-origin",
      });
      const data = await response.json();

      if (data.status === "success") {
        sweetAlert(data.status, data.message, data.status);
      } else {
        sweetAlert(data.status, data.message, data.status);
      }
    } catch (error) {
      console.error(error);
    }
  };

  loginForm.addEventListener("submit", async (event) => {
    event.preventDefault();

    const formData = new FormData(loginForm);
    const username = formData.get("username");
    const password = formData.get("password");
    const loginData = {
      username: username,
      password: password,
      login: true,
    };
    const data = await loginAPI(loginData);
  });

  
  //This function is for Sweet Alert
  function sweetAlert(title = null, text = null, icon = null) {
    Swal.fire({
      title: title === "success" ? "Success" : "Error",
      text: text,
      icon: icon,
      confirmButtonText: "Ok",
    }).then((result) => {
      if (result.isConfirmed && icon === "success") {
        const params = new URLSearchParams(window.location.search);
        const goto = params.get("goto");
        if (goto == "upload-properties") {
          window.location.href = "./upload-properties";
        } else if (goto == "upload-photos") {
          window.location.href = "./upload-photos";
        } else {
          window.location.href = "./";
        }
      }
    });
  }

  
  //Disabled Button When the username and password is empty
  const usernameInput = document.getElementById("username");
  const passwordInput = document.getElementById("password");
  const loginButton = document.getElementById("loginButton");

  loginButton.disabled = true;

  usernameInput.addEventListener(
    "input",
    () => (loginButton.disabled = !usernameInput.value.trim())
  );
  passwordInput.addEventListener(
    "input",
    () => (loginButton.disabled = !passwordInput.value.trim())
  );
});


